import React, { Component } from 'react'
import { Container }        from 'react-grid-system'
import { Col }              from 'react-grid-system'
import { Hidden }           from 'react-grid-system'
import { Visible }          from 'react-grid-system'
import { Row }              from 'react-grid-system'

import { Header }           from 'semantic-ui-react'
import { Icon }             from 'semantic-ui-react'
import { Segment }          from 'semantic-ui-react'


const SectionUserManager = ({ theme = {} }) => {
  const {
    BlockPageFooterLinks,
    BlockPageFooterSocial,
  } = theme

  if (!BlockPageFooterLinks || !BlockPageFooterSocial) {
    return (
      <Segment placeholder>
        <Header icon>
          <Icon name='pdf file outline' />
          SectionPageHeader cannot find BlockPageFooterLinks or BlockPageFooterSocial
        </Header>
      </Segment>
    )
  }

  return (
    <Container fluid style={{ padding: 20 }}>
      <Row justify='center'>
        <Col sm={5} lg={3}>
          <BlockPageFooterLinks />
        </Col>
        <Visible xs>
          <Col style={{ height: 10 }}>
          </Col>
        </Visible>
        <Col sm={5} lg={3}>
          <BlockPageFooterSocial />
        </Col>
      </Row>
    </Container>
  )
}

export default SectionUserManager