import React, { Component } from 'react'
import { Container }        from 'react-grid-system'
import { Col }              from 'react-grid-system'
import { Hidden }           from 'react-grid-system'
import { Row }              from 'react-grid-system'

import { Header }           from 'semantic-ui-react'
import { Icon }             from 'semantic-ui-react'
import { Segment }          from 'semantic-ui-react'


const SectionPageHeader = ({ theme = {} }) => {
  const { BlockPageHeaderLogo, BlockPageHeaderMenu } = theme

  if (!BlockPageHeaderLogo || !BlockPageHeaderMenu) {
    return (
      <Segment placeholder>
        <Header icon>
          <Icon name='pdf file outline' />
          SectionPageHeader cannot find BlockPageHeaderLogo or BlockPageHeaderMenu
        </Header>
      </Segment>    
    )
  }

  return (
    <Container fluid>
      <Row justify='center'>
        <Hidden xs>
          <Col sm={2} lg={1}>
            <BlockPageHeaderLogo />
          </Col>
        </Hidden>
        <Col sm={8} lg={5}>
          <BlockPageHeaderMenu />
        </Col>
      </Row>
    </Container>
  )
}

export default SectionPageHeader