import React, {Component} from 'react'
import {Grid, InputBase, Button} from '@material-ui/core';
import compose from 'recompose/compose'
import {rem} from 'polished'
import styled from 'styled-components';
import {ButtonPrimary} from '../../customUI'


const Input = styled(({ width, align, ...other }) => (
  <InputBase {...other} />
))`
  && {
    margin: none;
    border: none;
    box-shadow: 1px 1px 0px #b1b1b1 inset, -1px -1px 0px #b1b1b1 inset;
    font-size: ${rem(18)};
    width: ${props => props.width || 'auto'};
    input {
      color: #000;
      text-align: ${props => props.align || 'left'};
      padding: 0.935rem;
    }
  }
`;


//style Buttons
const ButtonPrimaryCustom = styled(ButtonPrimary)`
  && {
    padding: 0.7rem 0;
  }
`;


const enhance = compose();

const FooterSocial = enhance(({
  mdUp, children
}) => {
  return (
    <Grid xs={12} sm={12} md item className={mdUp}>

      {children}

      <div>
        <Grid container direction={'row'} justify="center" spacing={16}>
          <Grid sm={6} xs={12} item>
            <Input
              bg="none"
              placeholder="This is some email"
              width="100%"
              variant="standard"
              name="value"
              label=""
              id="standard-name"
              value={''}
              margin="none"
            />
          </Grid>
          <Grid sm={6} xs={12} item>
            <ButtonPrimaryCustom fullWidth variant="contained" color="primary">Subscribe</ButtonPrimaryCustom>
          </Grid>
        </Grid>
      </div>

    </Grid>
  )
})

export default FooterSocial

