import React, {Component} from 'react'
import {Grid, SvgIcon} from '@material-ui/core';
import compose from 'recompose/compose'
import VideoBackground from 'app/honey/video/web/VideoBackground'
import {SectionVideo, ButtonHeader, TagLine} from '../../customUI.js'
import {IconLogo} from '../../icons'
import styled from 'styled-components'
import {mediaGrid} from '../../customUI'
import ContainerDimensions from 'react-container-dimensions'


const MainGrid = styled(({ ...other }) => (
  <Grid {...other} />
))`
  && {
  height: ${props => props.height};
  min-height: 630px;
 `;

const HeaderGrid = styled(({ ...other }) => (
  <Grid {...other} />
))`
&& {
  color: ${props => props.color};
 
  ${mediaGrid(`xl`)}{//lg d
    height: 33%
  }
  ${mediaGrid(`lg`)}{//sm d
    height: 33%;
    &.footGrid {
      height: 33%
    }
  }
  ${mediaGrid(`md`)}{//tablet
    height: 25%;
    &.footGrid {
      height: 50%
    }
  }
  ${mediaGrid(`sm`)}{//phone
    height: 25%;
    &.footGrid {
      height: 50%
    }
  }
}
`;

const enhance = compose();

const Header = enhance(() => {
  return (
    <SectionVideo>

      <React.Fragment>

        <ContainerDimensions>
          {({ width: widthSource, height }) => {

            const width = widthSource < 1480 ? 1480 : widthSource
            const heightCustom = height * (width / 1480)
            const marginLeft = widthSource < 740 ? -1480 / 2 + widthSource / 2 : 0

            return (
              <VideoBackground width={width} heightCustom={heightCustom} marginLeft={marginLeft}/>
            )
          }}
        </ContainerDimensions>


        <MainGrid container direction={'row'}
                  justify={'space-between'}
                  alignItems={'center'}
                  spacing={0}
                  height={'100%'}
        >

          <HeaderGrid item xs={12} style={{ textAlign: 'center', fontSize: '221px' }}>
            <IconLogo fontSize="inherit" style={{ margin: '0 auto' }}/>
          </HeaderGrid>

          <HeaderGrid item xs={12}>
            <Grid container alignItems={'center'} justify='center'>
              <Grid item xs={12} sm={6}>
                <TagLine color="#fff" size="1.875em" fweight="300" className={'TagLine'}>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit. Maecenas fringilla massa vitae nisl
                  scelerisque lobortis.
                </TagLine>
              </Grid>
            </Grid>
          </HeaderGrid>

          <HeaderGrid item xs={12} className={'footGrid'}>
            <Grid container alignItems={'center'} justify='center' spacing={0}>
              {['subscribe', 'show', 'our story'].map((value, index) => (
                <Grid key={index} item>
                  <ButtonHeader variant="contained">
                    {value}
                  </ButtonHeader>
                </Grid>
              ))}
            </Grid>
          </HeaderGrid>

        </MainGrid>

      </React.Fragment>

    </SectionVideo>
  )
})

export default Header