import React, {Component} from 'react'
import {Grid, Typography} from '@material-ui/core'
import compose from 'recompose/compose'
import './main.scss'
import styled from 'styled-components'
import {rem} from 'polished'
import {IconFacebook} from '../../icons'

const enhance = compose()

const Title = styled(({ color, transform, border, margin, fweight, ...other }) => (
  <Typography {...other} />
))`&& {
  color: ${props => props.color};
  border-bottom: ${props => props.border};
  text-transform: ${props => props.transform || 'inherit'};
  border-color: #000;
  margin: ${props => props.margin};
  font-weight: ${props => props.fweight || '700'};
  min-width: auto;
  width: 100%;
  text-align: center;
  font-size: ${rem(35)};

}
`;


const Social = enhance(() => {
  return (
    <div>
      <Title transform="uppercase" margin="0 0 2.25rem 0">
        Sozial Buzz
      </Title>

      <Grid container direction={'row'} justify="space-between" alignItems={'baseline'} spacing={0}>
        {['instagram', 'fb', 'youtube', 'tw'].map((item, index) => {
          return (
            <Grid md={3} sm={6} xs={12} key={index} item className={'socialThumbnail'}>
              <a href={`//www.instagram.com/p/Bp9tfPzDdbQ/`} target="_blank" rel="noopener noreferrer"
                 className={'socialThumbnailLink'}>
                <img src={'/img/social' + (index + 1) + '.jpg?v=1'} alt="" className={'socialThumbnailImg'}/>
                {/* {item=== 'fb' ? <IconFacebook/>: null} */}
              </a>
            </Grid>
          )
        })}
      </Grid>
    </div>
  )
})

export default Social