import React, { Component } from 'react'
import { PoseGroup }        from 'react-pose'
import compose              from 'recompose/compose'
import lifecycle            from 'recompose/lifecycle'
import styled               from 'styled-components'


import NotifyList           from 'app/default/notify/web/NotifyList'
import withSpace            from 'evoke-me/space/all/withSpace'

import spaceNotify          from '../../space/spaceNotify'


const enhance = compose(
  withSpace(spaceNotify),
  // lifecycle({
  //   componentDidMount() {
  //     const { fromNotify } = this.props

  //     fromNotify.create({ message: `Hello` })
  //     fromNotify.create({ message: `Hello` })
  //   }
  // })
)

const BlockNotify = enhance(({
  fromNotify,
}) => {
  const { data, orderMap } = fromNotify

  return (
    <NotifyList
      data={data}
      order={orderMap.default}

      onClose={({ id }) => fromNotify.orderMove(id, `history`)}
    />
  )
})

export default BlockNotify