import controlTypeMap       from 'box/3d/control/api/controlTypeMap'

import config               from '../../config/api/config'
import statusMap            from '../api/statusMap'


export default [
  config.namespace,
  `editor`,
  [],
  () => ({
    status: statusMap.DEFAULT,
    controlType: controlTypeMap.PLANE,
  }),
  {
    statusSet: (getState, setState) => function* (value) {
      yield setState({ status: value })
    },
    controlTypeSet: (getState, setState) => function* (value) {
      yield setState({ controlType: value })
    },    
  }
]