import React, { Component } from 'react'
import styled               from 'styled-components'


import statusMap            from '../api/statusMap'


const Root = styled.div`
  background-color: rgba(255, 255, 255, 0.2);
  border-radius: 10px;

  color: rgba(255, 255, 255, 0.6);

  padding: 10px;

  pointer-events: none;
`

const map = {
  [statusMap.DEFAULT]: `EDITOR`,
  [statusMap.AREA_CREATE]: `AREA CREATE`,
}

export default ({
  status,
  ...props,
}) => {
  return (
    <Root {...props}>
      {map[status] || map[statusMap.DEFAULT]}
    </Root>
  )
}