import React, { Component } from 'react'
import compose              from 'recompose/compose'
import lifecycle            from 'recompose/lifecycle'

import * as THREE           from 'three'


import Context              from '../../scene/all/Context'


const enhance = compose(
  lifecycle({
    componentDidMount() {
      const { fromScene } = this.props
      const { scene } = fromScene

      const light = new THREE.AmbientLight(0x666666, 10)

      scene.add(light)
    }
  }),
)

const LightAmbient3d = enhance(() => null)

export default (props) => {
  return (
    <Context.Consumer>
      {(context) => (
        <LightAmbient3d {...props} fromScene={context} />
      )}
    </Context.Consumer>
  )
}